/*			InOut.c							*//*								 7 Oct 87	*//*						rev		15 Oct 87	*/static int errorSum = 0; 				/* Global variable keeps running total of error codes. */static int	width = 10,  dec = 3;void Wait(){	char	ch;		printf ("Type any character to continue...\n");	scanf ("%c", &ch);}void PromptInt (str, i)char *str;int *i;{	printf ("\n%s", str); printf (" -> ");	scanf ("%d", i);}int ErrorSum(){	return (errorSum);}void SetErrorSum (value)int value;{	errorSum = value;}void PostErrorMessage (code, message)int code;char *message;{	errorSum += code;	printf ("\n****************\n");	printf ("%s\n", message);	printf ("    with errorSum = %d\n", errorSum);	Wait();	printf ("\n****************\n");}void SetPrintFormat(fieldWidth, nDec)int fieldWidth, nDec;{	width = fieldWidth;	dec = nDec;}void PrintVector (vec, str)VECTOR *vec;char *str;{	PrintVec (vec->pntr, vec->len, str);}void PrintVec (vec, len, str)double *vec;int len;char *str;{	int i;		printf ("------------- V [%3d] %s\n",len, str);	for (i=0; i<len; ++i)		printf ("[%3d] %8.3f\n", i, *vec++);	Wait();}void PrintMatrix (mat, str)MATRIX *mat;char *str;{	PrintMat (mat->pntr, mat->nRows, mat->nCols, str);}void PrintMat (mat, nRows, nCols, str)double *mat[];int nRows, nCols;char *str;{	int		i, j;	printf("------------- M [%3d, %3d] %s\n",nRows, nCols, str);	for (i=0; i<nRows; ++i)	{	printf ("[%3d] ", i);		for (j=0; j<nCols; ++j)			printf ("%8.3f ", mat[j][i]);		printf ("\n");	}	Wait();}void PrintCube (theCube, message)CUBE *theCube;char *message;{	int p, r, c;		printf ("\n---------- Cube ---- %s\n", message);	for (p=0; p<theCube->nPlanes; ++p)	{	printf ("   ------  Plane #%d \n",p);		for (r=0; r<theCube->nRows; ++r)		{	printf ("[%2d]", r);			for (c=0; c<theCube->nCols; ++c)				printf(" %8.3f ", theCube->pntr[p][c][r]);			printf ("\n");		}	}	Wait();}	/*  PrintCube  */